import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend } from 'recharts';
import './App.css';

const API_BASE_URL = 'http://localhost:8000/api/v1';

function App() {
  const [prediction, setPrediction] = useState(null);
  const [historicalData, setHistoricalData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const [predictionRes, historicalRes] = await Promise.all([
          axios.get(`${API_BASE_URL}/predict`),
          axios.get(`${API_BASE_URL}/historical-data`)
        ]);
        setPrediction(predictionRes.data);
        setHistoricalData(historicalRes.data);
      } catch (err) {
        setError(err.message);
        console.error('Error fetching data:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
    // Refresh data every 5 minutes
    const interval = setInterval(fetchData, 5 * 60 * 1000);
    return () => clearInterval(interval);
  }, []);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="App">
      <header className="App-header">
        <h1>Sensex Predictor</h1>
      </header>

      <main>
        {prediction && (
          <div className="prediction-section">
            <h2>Market Prediction</h2>
            <div className="metrics">
              <div className="metric">
                <h3>Predicted Close</h3>
                <p>₹{prediction.predicted_close.toLocaleString()}</p>
              </div>
              <div className="metric">
                <h3>Trend</h3>
                <p className={prediction.trend.toLowerCase()}>{prediction.trend}</p>
              </div>
              <div className="metric">
                <h3>Confidence</h3>
                <p>{(prediction.confidence * 100).toFixed(1)}%</p>
              </div>
            </div>
          </div>
        )}

        {historicalData.length > 0 && (
          <div className="chart-section">
            <h2>Historical Data</h2>
            <LineChart width={800} height={400} data={historicalData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="timestamp" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Line type="monotone" dataKey="close" stroke="#8884d8" name="Close Price" />
              <Line type="monotone" dataKey="predicted_close" stroke="#82ca9d" name="Predicted" />
            </LineChart>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
